robotThemeViewBase::~robotThemeViewBase()
{
    touchgfx::CanvasWidgetRenderer::resetBuffer();
}

void robotThemeViewBase::setupScreen()
{
    scrollList1.initialize();
    for (int i = 0; i < scrollList1ListItems.getNumberOfDrawables(); i++)
    {
        scrollList1ListItems[i].initialize();
    }
}

void robotThemeViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &buttonTheme)
    {
        //changeToCustom
        //When buttonTheme clicked change screen to customTheme
        //Go to customTheme with no screen transition
        application().gotocustomThemeScreenNoTransition();
    }
    if (&src == &buttonEyes)
    {
        //goToEyeDemo
        //When buttonEyes clicked change screen to Screen1
        //Go to Screen1 with screen transition towards North
        application().gotoScreen1ScreenSlideTransitionNorth();
    }
}

void robotThemeViewBase::handleTickEvent()
{
    frameCountBlinkInterval++;
    if(frameCountBlinkInterval == TICK_BLINK_INTERVAL)
    {
        //blink
        //When every N tick play video videoBlink
        //Play video on videoBlink
        videoBlink.play();
        frameCountBlinkInterval = 0;
    }
}

void robotThemeViewBase::updateItemCallbackHandler(touchgfx::DrawableListItemsInterface* items, int16_t containerIndex, int16_t itemIndex)
{
    if (items == &scrollList1ListItems)
    {
        scrollList1UpdateItem(scrollList1ListItems[containerIndex], itemIndex);
    }
}
